mapper.select:
  		select * from member
  		where id=#{id} 
		and password=#{password};

mapper.update:
update project
set cnt = cnt + 1
where bno = #{bno}

update project
set
	content=#{content}
where bno=#{bno}

mapper.select:
  	select id from member
  		where name=#{name} 
		and email=#{email}

mapper.update:
update member 
set password=#{password}, 
email=#{email},
name=#{name}
where id=#{id};
